const btnBurger=document.querySelector(".btn--burger"),body=document.body,onBtnMenuOpened=()=>{body.classList.toggle("menu-opened")},onEscMenuClosed=e=>{27===e.keyCode&&body.classList.contains("menu-opened")&&body.classList.remove("menu-opened")};document.addEventListener("keydown",onEscMenuClosed),btnBurger.addEventListener("click",onBtnMenuOpened);